import React from "react";
import Typist from "react-typist";
import Container from "react-bootstrap/Container";
import Jumbotron from "react-bootstrap/Jumbotron";

const StarFive = () => {
    return (
        <View style={styles.starfive}>
            <TriangleUp style={styles.starfiveTop} />
            <View style={styles.starfiveBefore} />
            <View style={styles.starfiveAfter} />
        </View>
    );
};

StyleSheet.create({
    starfive: {
        width: 150,
        height: 150,
    },
    starfiveTop: {
        position: "absolute",
        top: -45,
        left: 37,
    },
    starfiveBefore: {
        backgroundColor: "transparent",
        position: "absolute",
        left: 0,
        top: 0,
        borderStyle: "solid",
        borderRightWidth: 100,
        borderRightColor: "transparent",
        borderBottomWidth: 70,
        borderBottomColor: "red",
        borderLeftWidth: 100,
        borderLeftColor: "transparent",
        transform: [{ rotate: "35deg" }],
    },
    starfiveAfter: {
        backgroundColor: "transparent",
        position: "absolute",
        top: 0,
        left: -25,
        width: 0,
        height: 0,
        borderStyle: "solid",
        borderRightWidth: 100,
        borderRightColor: "transparent",
        borderBottomWidth: 70,
        borderBottomColor: "red",
        borderLeftWidth: 100,
        borderLeftColor: "transparent",
        transform: [{ rotate: "-35deg" }],
    },
});
const MainBody = React.forwardRef(
    ({ gradient, title, message, icons }, ref) => {
        return (
            <Jumbotron
                fluid
                id="home"
                style={{
                    background: `linear-gradient(136deg,${gradient})`,
                    backgroundSize: "1200% 1200%",
                }}
                className="title bg-transparent bgstyle text-light min-vh-100 d-flex align-content-center align-items-center flex-wrap m-0"
            >
                <div id = "stars"/>
                <Container className="text-center" style = {{ borderRadius: '50%',
                    paddingTop: '10px', paddingBottom: '20px', backgroundColor:'rgb(9,9,121,.3)'}}>
                    <StarFive/>
                    <h1 ref={ref} className="display-1">
                        {title}
                    </h1>
                    <Typist className="lead typist" cursor={{ show: false }}>
                        {" "}
                        {message}
                    </Typist>
                    <div className="p-5">
                        {icons.map((icon, index) => (
                            <a
                                key={`social-icon-${index}`}
                                target="_blank"
                                rel="noopener noreferrer"
                                href={icon.url}
                                aria-label={`My ${icon.image.split("-")[1]}`}
                            >
                                <i className={`fab ${icon.image}  fa-3x socialicons`} />
                            </a>
                        ))},

                    </div >
                    <a
                        className="btn btn-outline-light btn-lg "
                        href="#aboutme"
                        role="button"
                        aria-label="Resume"
                        style = {{margin: '5px'}}
                    >
                        Resume
                    </a>
                    <a
                        className="btn btn-outline-light btn-lg "
                        href="#aboutme"
                        role="button"
                        aria-label="Portfolio"
                        style = {{margin: '5px'}}
                    >
                        Portfolio
                    </a>
                    <a
                        className="btn btn-outline-light btn-lg "
                        href="#aboutme"
                        role="button"
                        aria-label="Art"
                        style = {{margin: '5px'}}
                    >
                        Creative Work
                    </a>
                    <a className="btn btn-outline-light btn-lg"
                    role="button"
                       style = {{margin: '5px'}}
                    href="mailto:szassman@uwaterloo.ca"> Email me </a>
                </Container>
            </Jumbotron>
        );
    }
);

export default MainBody;
